// Get URL from env
const url = env.get("DISCORD_WEBHOOK_URL")

// Expect msg.payload from MQTT to be your alert text (string or JSON).
const alertText = (typeof msg.payload === "string")
  ? msg.payload
  : JSON.stringify(msg.payload);

// Set up the HTTP request properties
msg.method = "POST";
msg.url = url
msg.headers = { "Content-Type": "application/json" };

// Discord webhook basic payload
msg.payload = { content: "[Room Sensor Alert] " + alertText };

return msg;
